sequential.script:
tmp = 0;
switch ("@{operation}")
{
 case "+" :
  tmp = parseInt("@{operator1}") + parseInt("@{operator2}");
  break;
 case "-" :
  tmp = parseInt("@{operator1}") - parseInt("@{operator2}");
  break;
 case "*" :
  tmp = parseInt("@{operator1}") * parseInt("@{operator2}");
  break;
 case "/" :
  tmp = parseInt("@{operator1}") / parseInt("@{operator2}");
  break;
}
project.setProperty("@{result}", tmp);
